mapper.select:
	 	SELECT
			ordr_num, sub_nm, sub_id, whs_encd,invty_cls.invty_cls_encd,
	invty_cls.invty_cls_nm, measr_corp_nm, spc_model, invty_cd, 
		    invty_nm, bat_num, invty_encd, qty, uprc, amt, make_vouch_id, make_vouch_abst, send_unit_price, 
		    send_qty, send_moeny, in_unit_price, in_qty, in_moeny, oth_unit_price, oth_moeny, 
		    oth_qty, acct_yr, accti_mth, acc_num, acc_time, ctime, is_final_deal, is_mth_end_stl,it.cust_id,cd.cust_nm
		FROM
			invty_send_mth_term_bgn_tab AS it
		INNER JOIN cust_doc cd ON it.cust_id = cd.cust_id
		LEFT JOIN invty_cls ON invty_cls.invty_cls_encd = it.invty_cls_encd

	 	WHERE 1=1

			AND it.invty_Encd = #{invtyEncd}


			AND it.whs_encd = #{whsEncd}


			AND it.bat_num=#{batNum}


			AND it.acct_yr = #{year}


			AND it.accti_mth >= #{start}


			AND it.accti_mth <= #{end}


			AND it.accti_mth = #{month}


			AND it.is_final_deal = #{isFinalDeal}


			AND it.is_mth_end_stl = #{isMthEndStl}


			AND it.cust_id = #{custId}


			AND it.invty_encd IN

				#{invtyEncd}



			AND it.whs_encd IN 

                #{item.whsEncd}



			AND it.bat_num IN 

                #{item.batNum}



			AND it.invty_encd IN 

                #{item.invtyEncd}



			AND it.invty_cls_encd IN 

                #{item.invtyClsEncd}



			AND it.cust_id IN

                #{item.projEncd}



			AND it.whs_encd IN 

                #{whsEncd}



			AND it.bat_num IN 

					#{batNum}



			AND it.invty_encd IN 

					#{invtyEncd}


		ORDER BY it.accti_mth asc

	 	SELECT
		  ordr_num, sub_nm, sub_id, whs_encd,invty_cls.invty_cls_encd,invty_cls.invty_cls_nm, measr_corp_nm, spc_model, invty_cd, 
		  invty_nm, bat_num, invty_encd, SUM(qty) as qty, SUM(amt)/SUM(qty) as uprc, SUM(amt) as amt, make_vouch_id, make_vouch_abst,
          send_unit_price,send_qty, send_moeny, in_unit_price, in_qty, in_moeny, oth_unit_price, oth_moeny, 
		  oth_qty, acct_yr, accti_mth, acc_num, acc_time, ctime, is_final_deal, is_mth_end_stl,it.cust_id,cd.cust_nm
		FROM
			invty_send_mth_term_bgn_tab AS it
		INNER JOIN cust_doc cd ON it.cust_id = cd.cust_id
		LEFT JOIN invty_cls ON invty_cls.invty_cls_encd = it.invty_cls_encd

	 	WHERE 1=1

			AND it.invty_Encd = #{invtyEncd}


			AND it.whs_encd = #{whsEncd}


			AND it.bat_num=#{batNum}


			AND it.acct_yr = #{year}


			AND it.accti_mth >= #{start}


			AND it.accti_mth <= #{end}


			AND it.accti_mth = #{month}


			AND it.is_final_deal = #{isFinalDeal}


			AND it.is_mth_end_stl = #{isMthEndStl}


			AND it.cust_id = #{custId}


			AND it.invty_encd IN

				#{invtyEncd}



			AND it.whs_encd IN 

                #{item.whsEncd}



			AND it.bat_num IN 

                #{item.batNum}



			AND it.invty_encd IN 

                #{item.invtyEncd}



			AND it.invty_cls_encd IN 

                #{item.invtyClsEncd}



			AND it.cust_id IN

                #{item.projEncd}



			AND it.whs_encd IN 

                #{whsEncd}



			AND it.bat_num IN 

					#{batNum}



			AND it.invty_encd IN 

					#{invtyEncd}


		ORDER BY it.accti_mth asc

  		select count(*) from invty_send_mth_term_bgn_tab as it 
	 	WHERE 1=1

			AND it.invty_Encd = #{invtyEncd}


			AND it.whs_encd = #{whsEncd}


			AND it.bat_num=#{batNum}


			AND it.acct_yr = #{year}


			AND it.accti_mth = #{month}


			AND it.is_final_deal = #{isFinalDeal}


			AND it.is_mth_end_stl = #{isMthEndStl}

  		select * from invty_send_mth_term_bgn_tab as it 
	 	WHERE 1=1

			AND it.invty_Encd = #{invtyEncd}


			AND it.whs_encd = #{whsEncd}


			AND it.bat_num=#{batNum}


			AND it.acct_yr = #{year}


			AND it.accti_mth = #{month}


			AND it.is_final_deal = #{isFinalDeal}


			AND it.is_mth_end_stl = #{isMthEndStl}

SELECT ordr_num, sub_nm, sub_id, whs_encd, invty_cls_encd, measr_corp_nm, spc_model, invty_cd, invty_nm, bat_num, invty_encd, qty, uprc, amt, make_vouch_id, make_vouch_abst, send_unit_price, send_qty, send_moeny, in_unit_price, in_qty, in_moeny, oth_unit_price, oth_moeny, oth_qty, acct_yr, accti_mth, acc_num, acc_time, ctime, is_final_deal, is_mth_end_stl FROM invty_send_mth_term_bgn_tab AS it WHERE 1 = 1
AND it.is_final_deal = #{isFinalDeal}
AND it.acct_yr = #{year}
AND it.accti_mth = #{month}
AND invty_cls_encd = #{invtyClsEncd}
GROUP BY invty_encd

mapper.update:
  		update invty_send_mth_term_bgn_tab 



					when ordr_num = #{item.ordrNum} then #{item.isMthEndStl}




					when ordr_num = #{item.ordrNum} then #{item.inMoeny}




					when ordr_num = #{item.ordrNum} then #{item.inQty}




					when ordr_num = #{item.ordrNum} then #{item.inUnitPrice}




					when ordr_num = #{item.ordrNum} then #{item.sendMoeny}




					when ordr_num = #{item.ordrNum} then #{item.sendQty}




					when ordr_num = #{item.ordrNum} then #{item.sendUnitPrice}




					when ordr_num = #{item.ordrNum} then #{item.othMoeny}




					when ordr_num = #{item.ordrNum} then #{item.othQty}




					when ordr_num = #{item.ordrNum} then #{item.othUnitPrice}




		where ordr_num in

			#{item.ordrNum}

mapper.delete:
  		DELETE FROM invty_send_mth_term_bgn_tab
  		where ordr_num in

			#{item.ordrNum}

mapper.select:
  		SELECT
			it.whs_encd,it.invty_encd,it.send_unit_price,it.in_unit_price,it.oth_unit_price,it.uprc,it.ordr_num,it.bat_num,it.send_moeny,
			it.send_qty,it.in_moeny,it.in_qty,it.oth_moeny,it.oth_qty,it.qty,it.amt,it.measr_corp_nm,it.acct_yr,it.accti_mth,
			wd.whs_nm,io.weight,io.bx_rule,cls.invty_cls_nm as invty_cls_nm,cls.invty_cls_encd as invty_cls_encd,cls.level as level,io.invty_nm,
			io.weight,io.bx_rule,ac.subj_nm as sub_nm,ac.subj_id as subj_id
		FROM	invty_send_mth_term_bgn_tab  it
		LEFT JOIN whs_doc wd ON wd.whs_encd = it.whs_encd
		LEFT JOIN invty_doc io ON io.invty_encd = it.invty_encd
		LEFT JOIN invty_cls cls ON cls.invty_cls_encd = it.invty_cls_encd 
	    LEFT JOIN invty_subj_set_tab as a ON a.invty_big_cls_encd = it.invty_cls_encd 
        LEFT JOIN acct_itm_doc as ac ON ac.subj_id = a.invty_subj_id
		WHERE 1=1

			AND it.whs_encd = #{whsEncd}


			AND it.bat_num = #{batNum}


			AND it.invty_encd =#{invtyEncd}


			AND it.acct_yr = #{year}


			AND it.accti_mth = #{month}


			AND it.accti_mth >= #{start}


			AND it.accti_mth <= #{end}


			AND ac.subj_id = #{subjId}

		ORDER BY it.acct_yr,it.accti_mth
  		LIMIT #{index},#{num}

SELECT it.whs_encd,it.invty_encd,it.send_unit_price,it.in_unit_price,it.oth_unit_price,it.uprc, it.ordr_num,it.bat_num,SUM(it.send_moeny) as send_moeny,SUM(it.send_qty) as send_qty,SUM(it.in_moeny) as in_moeny, SUM(it.in_qty) as in_qty,SUM(it.oth_moeny) as oth_moeny,SUM(it.oth_qty) as oth_qty,SUM(it.qty) qty,SUM(it.amt) amt, it.measr_corp_nm,it.acct_yr,it.accti_mth,wd.whs_nm,io.weight,io.bx_rule,cls.invty_cls_nm AS invty_cls_nm, cls.invty_cls_encd AS invty_cls_encd,cls.LEVEL AS LEVEL,io.invty_nm,io.weight,io.bx_rule,ac.subj_nm AS sub_nm, ac.subj_id AS subj_id,cust.cust_nm AS cust_nm,cust.cust_id AS cust_id FROM invty_send_mth_term_bgn_tab it LEFT JOIN whs_doc wd ON wd.whs_encd = it.whs_encd LEFT JOIN invty_doc io ON io.invty_encd = it.invty_encd LEFT JOIN invty_cls cls ON cls.invty_cls_encd = it.invty_cls_encd LEFT JOIN invty_subj_set_tab as a ON a.invty_big_cls_encd = it.invty_cls_encd LEFT JOIN acct_itm_doc as ac ON ac.subj_id = a.invty_subj_id LEFT JOIN cust_doc cust ON cust.cust_id = it.cust_id WHERE 1=1
AND it.whs_encd = #{whsEncd}
AND it.bat_num = #{batNum}
AND it.invty_encd =#{invtyEncd}
AND it.acct_yr = #{year}
AND it.accti_mth = #{month}
AND it.accti_mth >= #{start}
AND it.accti_mth <= #{end}
AND ac.subj_id = #{subjId}
GROUP BY it.whs_encd,it.invty_encd
GROUP BY it.invty_encd
GROUP BY it.cust_id,it.invty_encd
ORDER BY it.acct_yr,it.accti_mth

  		SELECT COUNT(*) FROM (
			SELECT SUM(it.whs_encd)

			FROM	invty_send_mth_term_bgn_tab  it
			LEFT JOIN whs_doc wd ON wd.whs_encd = it.whs_encd
			LEFT JOIN invty_doc io ON io.invty_encd = it.invty_encd
			LEFT JOIN invty_cls cls ON cls.invty_cls_encd = it.invty_cls_encd 
		    LEFT JOIN invty_subj_set_tab as a ON a.invty_big_cls_encd = it.invty_cls_encd 
	        LEFT JOIN acct_itm_doc as ac ON ac.subj_id = a.invty_subj_id
	        LEFT JOIN cust_doc cust ON cust.cust_id = it.cust_id 
			WHERE 1=1

				AND it.whs_encd = #{whsEncd}


				AND it.bat_num = #{batNum}


				AND it.invty_encd =#{invtyEncd}


				AND it.acct_yr = #{year}


				AND it.accti_mth = #{month}


				AND it.accti_mth >= #{start}


				AND it.accti_mth <= #{end}


				AND ac.subj_id = #{subjId}


				GROUP BY it.whs_encd,it.invty_encd


				GROUP BY it.invty_encd


				GROUP BY it.cust_id,it.invty_encd

  		) a
