FROM business_type 
WHERE bus_type_id = #{busTypeId}

mapper.update:
UPDATE business_type 
SET bus_type_id=#{busTypeId}, bus_type_name=#{busTypeName}, memo=#{memo}
WHERE bus_type_id = #{busTypeId}

